def get_legend_by_title(title: str):
    """
    Get a specific legend by title
    
    Args:
        title: Title of the legend
        
    Returns:
        Dict or None: Legend data if found
    """
    for legend in CI_LEGENDS:
        if legend["title"] == title:
            return legend
    return None
